function format_date($var) {
    return date('d/m/Y H:i:s', strtotime($var));
}

function check_access($role_id, $menu_id)
{
    $ci = get_instance();

    $ci->db->where('role_sid', $role_id);
    $ci->db->where('menu_sid', $menu_id);
    $result = $ci->db->get('user_access_menu');

    if ($result->num_rows() > 0) {
        return "checked='checked'";
    }
    return "";
}
function put_user_last_connect($user_sid, $last_connect, $lat = null, $lon = null) {
    $ci = get_instance();

    $ci->db->where('user_sid', $user_sid);
    $ci->db->update('users', array(
        'last_connect' => $last_connect,
        'lat_connect' => $lat,
        'lon_connect' => $lon
    ));
}
function get_ulp_user() {
    $ci = get_instance();
    $ci->db->where('ref_sid', $ci->session->userdata('user_unit'));
    return $ci->db->get('generic_references')->row_array();
}
function uid_generator($prefix = false)
{
    if ($prefix) {
        return strtoupper($prefix . '-' . time());
    }
    return strtoupper('UID-' . time());
}
function get_times_now($format = NULL)
{
    if ($format) {
        return date($format, time());
    } else {
        return date('Y-m-d H:i:s', time());
    }
}
function verify_request()
{
    // Get all the headers
    $headers = $this->input->request_headers();

    // Extract the token
    $token = $headers['Authorization'];

    // Use try-catch
    // JWT library throws exception if the token is not valid
    try {
        // Validate the token
        // Successfull validation will return the decoded user data else returns false
        $data = auth::validateToken($token);
        if ($data == null) {
            $this->response(false, 'Unauthorized Access!', $data, Rest_lib::HTTP_UNAUTHORIZED);
            exit();
        }
        else if ($data === false) {
            $this->response(false, 'Unauthorized Access!', $data, Rest_lib::HTTP_UNAUTHORIZED);
            exit();
        }
        else {
            return $data;
        }
    } catch (Exception $e) {
        // Token is invalid
        // Send the unathorized access message
        header("Content-type:application/json");
        header(http_response_code(401));
        echo '{"status":false, "message":"Unauthorized Access!"}';
        exit();
    }
}
function role_level_indicator($val, $type) {
    $line = '';
    if ($val == 0) {
        $line .= "▪";
    } else {
        $line .= "";
    }
    for ($i = 0; $i < $val; $i++) {
        if ($type == 'ISADMIN') {
            if ($val != 0) {
                $line .= "━━";
            } else {
                $line = "";
            }
        } else {
           if ($i >= 3) {
               $line .= "";
           } else {
               $line .= "━━";
           }
        }
    }
    return $line;
}
